
// Scripts for Firebase. Using the compat libraries for simplicity in a service worker environment.
importScripts('https://www.gstatic.com/firebasejs/10.12.2/firebase-app-compat.js');
importScripts('https://www.gstatic.com/firebasejs/10.12.2/firebase-messaging-compat.js');

// This script is dynamically generated by /api/firebase-config.js and contains the config object
importScripts('/firebase-config.js');

// `self` is the ServiceWorkerGlobalScope. `firebaseConfig` is loaded from the script above.
if (self.firebaseConfig && self.firebaseConfig.projectId) {
  // Initialize the Firebase app in the service worker with the generated config
  const app = firebase.initializeApp(self.firebaseConfig);
  const messaging = firebase.messaging(app);

  // This handler is called when a notification is received while the app is in the background
  messaging.onBackgroundMessage((payload) => {
    console.log('[firebase-messaging-sw.js] Received background message: ', payload);

    const notificationTitle = payload.notification.title;
    const notificationOptions = {
      body: payload.notification.body,
      icon: payload.notification.icon || '/icon-192x192.png',
    };

    // The service worker displays the notification
    self.registration.showNotification(
      notificationTitle,
      notificationOptions
    );
  });
}
